mapper.update:
	update wm_review
	set title = #{title}, content = #{content} 
    where mno = #{mno} and review_no = #{review_no}

update wm_image
set img_url = #{img_url}
where content_no = #{content_no}

update wm_review_region
set region_name = #{region_name}
where review_no = #{review_no}

update wm_review
set hits = #{hits}
where review_no = #{review_no}

